var mongoose = require('mongoose');
var Schema = mongoose.Schema;

const RegisterUserSchema = new Schema({
    email: {
        type: String,
        required: true,
        unique: true,
    },
    username: {
        type: String,
        required: true,
        unique: true,
    },
    password: {
        type: String,
        required: true,
    },
});

module.exports = mongoose.model('RegisterUser', RegisterUserSchema);